export type EncryptionDetails = {
    encryptedData: string;
    key: string;
    salt: string;
};
export type DecryptionDetails = {
    decryptedData: string;
    key: string;
    salt: string;
};
export interface IEncrypto {
    encrypt: (data: string, password: string) => EncryptionDetails;
    encryptWithKey: (data: string, key: string, salt: string) => EncryptionDetails;
    decrypt: (data: string, password: string, salt: string) => DecryptionDetails;
    decryptWithKey: (data: string, key: string, salt: string) => DecryptionDetails;
}
