/**
 * Ensures the value is truthy, otherwise throws an error with the given message.
 *
 * @param {T} val - the value to ensure truthy
 * @param {string} message - the error message to throw if value is falsy
 * @return {NonNullable<T>} the value as NonNullable
 */
export declare const ensureVal: <T>(val: T, message: string) => NonNullable<T>;
